import React, { useReducer, useEffect } from "react";
import { CartActionTypes } from "../actions/cart.types";
import { addItemToCart, removeItemFromCart } from "../utils/cart.utils";
import { useLocalStorage } from "../hooks/useLocalStorage";

export const CartContext = React.createContext({});
export const CartConsumer = CartContext.Consumer;

const reducer = (cartState = { hidden: true, cartItems: [] }, action) => {
  switch (action.type) {
    case CartActionTypes.TOGGLE_CART_HIDDEN:
      return { ...cartState, hidden: !cartState.hidden };
    case CartActionTypes.ADD_ITEM:
      return {
        ...cartState,
        cartItems: addItemToCart(cartState.cartItems, action.payload)
      };
    case CartActionTypes.REMOVE_ITEM:
      return {
        ...cartState,
        cartItems: removeItemFromCart(cartState.cartItems, action.payload)
      };
    case CartActionTypes.CLEAR_ITEM_FROM_CART:
      let temp = cartState.cartItems.filter(
        cartItem => cartItem.id !== action.payload.id
      );
      setCartItemsLocalState(temp);

      return {
        ...cartState,
        cartItems: temp
      };
    default:
      return cartState;
  }
};

const CartProvider = props => {
  const [cartItemsLocalState, setCartItemsLocalState] = useLocalStorage(
    "cartItems"
  );

  const INITIAL_STATE = {
    hidden: true,
    cartItems: []
    // cartItems: cartItemsLocalState ? cartItemsLocalState : []
  };

  const [cartState, dispatch] = useReducer(reducer, INITIAL_STATE);

  //   useEffect(() => {
  //     setCartItemsLocalState(cartState.cartItems);
  //   }, [cartState.cartItems]);
  return (
    <CartContext.Provider value={{ cartState, dispatch }}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
