import { ShopContext } from "../../context/ShopProvider";

const CollectionPage = ({ match }) => {
  const { shopState, dispatch } = useContext(ShopContext);
  let data = shopState.find(
    collections =>
      collections.id === COLLECTION_ID_MAP[match.params.collectionId]
  );

  // let currentCollection = dispatch({
